import React, { useState } from 'react';
import '../index.css';
import '../modules.css';

const LegalSummarizationPage = () => {
  const [textInput, setTextInput] = useState('');
  const [output, setOutput] = useState('');

  const handleTextChange = (e) => {
    setTextInput(e.target.value);
  };

  const handleProcess = async () => {
    // Send a POST request to the Flask backend
    try {
      const response = await fetch('http://localhost:5000/summary', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: new URLSearchParams({ inputData: textInput }),
      });

      const result = await response.text();  // Assuming the backend sends plain text
      setOutput(result);  // Set the response text as output
    } catch (error) {
      console.error('Error:', error);
      setOutput('Failed to process the document.');
    }
  };

  return (
    <div className="page-container">
      <div className="content-container">
        <h1 className="title">Legal Text Summarization</h1>
        <div className="instructions">
          <p>Please enter or paste your legal document text to get a summarized output.</p>
        </div>
        <textarea
          value={textInput}
          onChange={handleTextChange}
          placeholder="Enter legal document text here..."
          className="text-input"
        />
        <button
          onClick={handleProcess}
          className="process-button"
        >
          Process Document
        </button>
        {output && (
          <div className="output-container">
            <h2 className="output-title">Output:</h2>
            <p className="output-text">{output}</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default LegalSummarizationPage;
