import React, { useState } from 'react';
import '../index.css';
import '../modules.css';

const RecommendCasesPage = () => {
  const [textInput, setTextInput] = useState('');
  const [output, setOutput] = useState(null);
  const [loading, setLoading] = useState(false);
  const [currentIndex, setCurrentIndex] = useState(0);

  const handleTextChange = (e) => {
    setTextInput(e.target.value);
  };

  const handleProcess = async () => {
    setLoading(true);
    try {
      const response = await fetch('/precedent', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/x-www-form-urlencoded',
        },
        body: new URLSearchParams({ inputData: textInput }),
      });

      if (response.ok) {
        const data = await response.json();
        setOutput(data);
        setCurrentIndex(0); // Reset to first output when new data is fetched
      } else {
        setOutput('Error: Unable to fetch recommended cases.');
      }
    } catch (error) {
      console.error('Error:', error);
      setOutput('Error: Unable to fetch recommended cases.');
    } finally {
      setLoading(false);
    }
  };

  const goNext = () => {
    if (output && currentIndex < output.length - 1) {
      setCurrentIndex(currentIndex + 1);
    }
  };

  const goPrevious = () => {
    if (currentIndex > 0) {
      setCurrentIndex(currentIndex - 1);
    }
  };

  const renderPrecedentOutput = (data) => {
    if (Array.isArray(data) && data.length > 0) {
      return (
        <>
          <textarea className="response-box" value={data[currentIndex]} readOnly />
          <div className="pagination-buttons">
            <button onClick={goPrevious} disabled={currentIndex === 0} className="pagination-button">
              Previous
            </button>
            <button onClick={goNext} disabled={currentIndex === data.length - 1} className="pagination-button">
              Next
            </button>
          </div>
        </>
      );
    }
    return <p className="output-text">Unexpected response format for precedent search</p>;
  };

  const renderOtherRoutesOutput = (data) => (
    <textarea className="response-box" value={JSON.stringify(data, null, 2)} readOnly />
  );

  return (
    <div className="page-container">
      <div className="content-container">
        <h1 className="title">Recommend Similar Cases</h1>
        <div className="instructions">
          <p>Enter or paste your document text to find similar past cases.</p>
        </div>
        <textarea
          value={textInput}
          onChange={handleTextChange}
          placeholder="Enter document text here..."
          id="inputData"
          className="text-input"
        />
        <button
          onClick={handleProcess}
          className="process-button"
          disabled={loading}
        >
          {loading ? 'Processing...' : 'Recommend Cases'}
        </button>
        {output && (
          <div className="output-container">
            <h2 className="output-title">Search Results:</h2>
            {Array.isArray(output) ? renderPrecedentOutput(output) : renderOtherRoutesOutput(output)}
          </div>
        )}
      </div>
    </div>
  );
};

export default RecommendCasesPage;
